package practice01;

public class SwitchAndForloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		

		for (int x = 1; x < 6; x++) {

			switch (x) {

			case 1:

				System.out.print(x);//12Done!Done!

				x++;

			case 2:

				System.out.print(x);

				x++;

				break;

			case 3:

				System.out.print(x);

				x += 2;

				break;

			default:

				System.out.print(" Done!");

			}
		}

		//////////////////
	}

}
